mapper.select:
select ID, USER_ID, CLASSNAME, STATE, FLOW_ID
  from WF_FLOW_LOG
 where FLOW_ID = #{flowId,jdbcType=VARCHAR}

select ID, USER_ID, CLASSNAME, STATE, FLOW_ID
  from WF_FLOW_LOG
 where ID = #{id,jdbcType=VARCHAR} 
   and USER_ID = #{userId}
   and CLASSNAME = #{classname}

mapper.delete:
delete from WF_FLOW_LOG
 where ID = #{id,jdbcType=VARCHAR} 
   and USER_ID = #{userId, jdbcType=VARCHAR}

   and CLASSNAME = #{classname, jdbcType=VARCHAR}

delete from WF_FLOW_LOG
where ID = #{id,jdbcType=VARCHAR} 
  and FLOW_ID = #{flowId, jdbcType=VARCHAR}

mapper.update:
update WF_FLOW_LOG
   set STATE=#{state}
 where FLOW_ID=#{flowId}
