mapper.insert:
insert into WF_FLOW_RESOURCE (APPID, DEPLOYID, PROCESSID,
  PROCESSNAME, APPCLASSNAME, MEMO
  )
values (#{appid,jdbcType=VARCHAR}, #{deployid,jdbcType=VARCHAR}, #{processid,jdbcType=VARCHAR}, 
  #{processname,jdbcType=VARCHAR}, #{appclassname,jdbcType=VARCHAR}, #{memo,jdbcType=VARCHAR}
  )

mapper.select:
select APPID, DEPLOYID, PROCESSID, PROCESSNAME, APPCLASSNAME, MEMO
from WF_FLOW_RESOURCE
where APPID = #{appid,jdbcType=VARCHAR}

select APPID, DEPLOYID, PROCESSID, PROCESSNAME, APPCLASSNAME, MEMO
from WF_FLOW_RESOURCE
where 1=1

    AND APPID = #{appid,jdbcType=VARCHAR}


    AND DEPLOYID = #{deployid,jdbcType=VARCHAR}


    AND PROCESSID = #{processid,jdbcType=VARCHAR}


    AND PROCESSNAME LIKE '%'||#{processname,jdbcType=VARCHAR}||'%'


    AND APPCLASSNAME LIKE '%'||#{appclassname,jdbcType=VARCHAR}||'%'


    AND MEMO LIKE '%'||#{memo,jdbcType=VARCHAR}||'%'

select APPID, DEPLOYID, PROCESSID, PROCESSNAME, APPCLASSNAME, MEMO
  from WF_FLOW_RESOURCE
 where APPCLASSNAME = #{appClassName,jdbcType=VARCHAR}

	select count(*)
	from WF_FLOW_RESOURCE wf
	where wf.APPCLASSNAME=#{appclassname,jdbcType=VARCHAR}

      AND APPID != #{appid,jdbcType=VARCHAR}
