mapper.select:
SELECT count(*) 
FROM DIM_IA_CONTENT_LABEL 
where 1=1 

	and CONTENT_LABEL_NAME1=#{contentLabelName1}




    and CONTENT_LABEL_NAME like #{contentLabelName} 


    and CONTENT_LABEL_CODE1 = #{contentLabelCode1} 



	and( 
		CONTENT_LABEL_NAME1 like  #{label} 
		or CONTENT_LABEL_NAME2 like  #{label}
		or CONTENT_LABEL_NAME3 like  #{label}
		or CONTENT_LABEL_NAME4 like  #{label}
		or CONTENT_LABEL_NAME5 like  #{label}
		or CONTENT_LABEL_NAME6 like  #{label}
	)


    and author like #{author} 


    and OP_TIME >= #{startTime} 


    and OP_TIME <= #{endTime}

	SELECT
  t1.ID,
  t1.CONTENT_LABEL_CODE,t1.CONTENT_LABEL_NAME,t1.CONTENT_LABEL_LEVEL,
  t1.CONTENT_LABEL_CODE1,t1.CONTENT_LABEL_NAME1,t1.CONTENT_LABEL_CODE2,t1.CONTENT_LABEL_NAME2,t1.CONTENT_LABEL_CODE3,t1.CONTENT_LABEL_NAME3,
  t1.CONTENT_LABEL_CODE4,t1.CONTENT_LABEL_NAME4,t1.CONTENT_LABEL_CODE5,t1.CONTENT_LABEL_NAME5,t1.CONTENT_LABEL_CODE6,t1.CONTENT_LABEL_NAME6,
  t1.AUTHOR,t1.OP_TIME,t2.CONTENT_LABEL_NAME CONTENT_LABEL_NAME_RELEVANCE
	FROM DIM_IA_CONTENT_LABEL t1
	left join DIM_IA_CONTENT_LABEL_RELEVANCE t2 on t1.CONTENT_LABEL_CODE=t2.CONTENT_LABEL_CODE
	where 1=1 		

		and t1.CONTENT_LABEL_NAME1=#{contentLabelName1}




	    and t1.CONTENT_LABEL_NAME like #{contentLabelName}


	    and t1.CONTENT_LABEL_CODE1 = #{contentLabelCode1}



		and(
		t1.CONTENT_LABEL_NAME1 like  #{label}
			or t1.CONTENT_LABEL_NAME2 like  #{label}
			or t1.CONTENT_LABEL_NAME3 like  #{label}
			or t1.CONTENT_LABEL_NAME4 like  #{label}
			or t1.CONTENT_LABEL_NAME5 like  #{label}
			or t1.CONTENT_LABEL_NAME6 like  #{label}
		)


	    and t1.author like #{author}


	    and t1.OP_TIME >= #{startTime}


	    and t1.OP_TIME <= #{endTime}

	order by t1.OP_TIME,t1.ID desc

    	select count(*) from DIM_IA_CONTENT_LABEL where 1=1

		    and ID=#{id}


			and CONTENT_LABEL_NAME1=#{contentLabelName1}


		    and CONTENT_LABEL_NAME2=#{contentLabelName2}


			and CONTENT_LABEL_NAME3=#{contentLabelName3}


		    and CONTENT_LABEL_NAME4=#{contentLabelName4}


			and CONTENT_LABEL_NAME5=#{contentLabelName5}


		    and CONTENT_LABEL_NAME6=#{contentLabelName6}


		    and CONTENT_LABEL_CODE=#{contentLabelCode}


			and CONTENT_LABEL_CODE1=#{contentLabelCode1}


		    and CONTENT_LABEL_CODE2=#{contentLabelCode2}


			and CONTENT_LABEL_CODE3=#{contentLabelCode3}


		    and CONTENT_LABEL_CODE4=#{contentLabelCode4}


			and CONTENT_LABEL_CODE5=#{contentLabelCode5}


		    and CONTENT_LABEL_CODE6=#{contentLabelCode6}


		    and CONTENT_LABEL_LEVEL=#{contentLabelLevel}

mapper.delete:
    delete from DIM_IA_CONTENT_LABEL
     where 1=1

			and CONTENT_LABEL_NAME1=#{contentLabelName1}


		    and CONTENT_LABEL_NAME2=#{contentLabelName2}


			and CONTENT_LABEL_NAME3=#{contentLabelName3}


		    and CONTENT_LABEL_NAME4=#{contentLabelName4}


			and CONTENT_LABEL_NAME5=#{contentLabelName5}


		    and CONTENT_LABEL_NAME6=#{contentLabelName6}

mapper.update:
update DIM_IA_CONTENT_LABEL  


		CONTENT_LABEL_NAME1=#{contentLabelName1Param},


		CONTENT_LABEL_NAME2=#{contentLabelName2},


		CONTENT_LABEL_NAME3=#{contentLabelName3},


		CONTENT_LABEL_NAME4=#{contentLabelName4},


		CONTENT_LABEL_NAME5=#{contentLabelName5},


		CONTENT_LABEL_NAME6=#{contentLabelName6},


where 

		CONTENT_LABEL_NAME1=#{contentLabelName}


		CONTENT_LABEL_NAME2=#{contentLabelName}


		CONTENT_LABEL_NAME3=#{contentLabelName}


		CONTENT_LABEL_NAME4=#{contentLabelName}


		CONTENT_LABEL_NAME5=#{contentLabelName}


		CONTENT_LABEL_NAME6=#{contentLabelName}

mapper.select:
    	select count(*) from DIM_IA_CONTENT_LABEL where 1=1

		    and ID!=#{id}


			and CONTENT_LABEL_NAME1=#{contentLabelName1Param}


		    and CONTENT_LABEL_NAME2=#{contentLabelName2}


			and CONTENT_LABEL_NAME3=#{contentLabelName3}


		    and CONTENT_LABEL_NAME4=#{contentLabelName4}


			and CONTENT_LABEL_NAME5=#{contentLabelName5}


		    and CONTENT_LABEL_NAME6=#{contentLabelName6}



				 and CONTENT_LABEL_NAME2 is null


				 and CONTENT_LABEL_NAME3 is null


				 and CONTENT_LABEL_NAME4 is null


				 and CONTENT_LABEL_NAME5 is null


				 and CONTENT_LABEL_NAME6 is null

		SELECT
			ID, 
			CONTENT_LABEL_CODE,CONTENT_LABEL_NAME,CONTENT_LABEL_LEVEL,
       		CONTENT_LABEL_CODE1,CONTENT_LABEL_NAME1,CONTENT_LABEL_CODE2,CONTENT_LABEL_NAME2,CONTENT_LABEL_CODE3,CONTENT_LABEL_NAME3,
       		CONTENT_LABEL_CODE4,CONTENT_LABEL_NAME4,CONTENT_LABEL_CODE5,CONTENT_LABEL_NAME5,CONTENT_LABEL_CODE6,CONTENT_LABEL_NAME6,
      		AUTHOR,OP_TIME
		FROM DIM_IA_CONTENT_LABEL 
		where 1=1 

		    and CONTENT_LABEL_LEVEL=#{contentLabelLevel} 


		    and CONTENT_LABEL_NAME=#{contentLabelName} 


		    and CONTENT_LABEL_NAME1=#{contentLabelName1} 


		    and CONTENT_LABEL_NAME2=#{contentLabelName2} 


		    and CONTENT_LABEL_NAME3=#{contentLabelName3} 


		    and CONTENT_LABEL_NAME4=#{contentLabelName4} 


		    and CONTENT_LABEL_NAME5=#{contentLabelName5} 


		    and CONTENT_LABEL_NAME6=#{contentLabelName6} 

		order by OP_TIME desc

mapper.delete:
