mapper.select:
select t.id, t.tar_base_name tarBaseName, t.tar_base_define tarBaseDefine, t.tar_base_type tarBaseType, t.tar_base_class tarBaseClass, t.create_user createUser, t.create_time createTime from DM_D_TAR_RULE_BASE t where 1 = 1
and t.tar_base_name like #{likeTarBaseName}
and t.tar_base_class like #{likeTarBaseClass}
and t.tar_base_name = #{tarBaseName}
and t.id != #{id}
