mapper.select:
select MONTH_ID as "month",to_char(nvl(sum(COLUMN1),0)) as "count",to_char(nvl(sum(COLUMN2),0)) as "val" from ${tableName} where to_number(MONTH_ID)<= to_number(#{date}) and to_number(MONTH_ID)>= to_number(#{oleDate}) and INDEX_LVL_1 in (${auditTypes})
and AREA_ID=#{orgId}
and ${where}
group by MONTH_ID order by to_number(MONTH_ID)

		select
    		to_char(count(distinct AREA_ID)) "countOrg"
    	from
     		${tableName} 
    	where
      		MONTH_ID = #{date}

			and ${where}

select t1."add",t1."cule",t2.name "name" from( select AREA_ID "add", to_char(nvl(sum(COLUMN1),0)) "cule" from ${tableName} where MONTH_ID = #{date} and INDEX_LVL_1 in (${auditTypes})
and ${where}
group by AREA_ID ) t1 join sys_org t2 on t1."add"=t2.ORG_ID order by to_number("cule") desc

select t1."add",t1."amount",t2.name "name" from( select AREA_ID "add", to_char(nvl(sum(COLUMN2),0)) "amount" from ${tableName} where MONTH_ID = #{date} and INDEX_LVL_1 in (${auditTypes})
and ${where}
group by AREA_ID ) t1 join sys_org t2 on t1."add"=t2.ORG_ID order by to_number("amount") desc

select t1.org_id "add",to_number(nvl(t2."value",0)) "value",to_number(nvl(t2."number",0)) "number",t1.name "name" from (select org_id,name from sys_org_area where org_id in (${citys}))t1 left join( select AREA_ID "add", to_char(nvl(sum(COLUMN1),0)) "value", to_char(nvl(sum(COLUMN2),0)) "number" from ${tableName} where MONTH_ID = #{date} and AREA_ID in (${citys}) and INDEX_LVL_1 in (${auditTypes})
and ${where}
group by AREA_ID )t2 on t1.org_id = t2."add" order by to_number("value") desc

		select * from (
			select  
				a."model",a."cule",a."name",to_char((a."cule"-b."cule")/b."cule") "mom" 
			from (
				select 
					t1."model",t1."cule",t2.MODEL_DESC "name" 
				from(
					select 
						* 
					from(
						select 
							INDEX_LVL_1 "model",to_char(nvl(sum(COLUMN1),0)) "cule"
        				from 
          					${tableName} 
        				where 
          					MONTH_ID =#{date}
        				and 
          					INDEX_LVL_1 in (${auditTypes})

							and ${where}


							and AREA_ID=#{orgId}

         				group by 
							INDEX_LVL_1 
					)
				) t1 
				join 
					aud_audit_model t2 
				on 
					t1."model" = t2.MODEL_ID 
				where 
					t2.AUDIT_TYPE=#{auditType} 
				and 
					t2.model_status='3'
			) a left join(
				select 
					t1."model",t1."cule",t2.MODEL_DESC "name" 
				from(
      				select 
      					* 
      				from(
        				select 
          					INDEX_LVL_1 "model",to_char(nvl(sum(COLUMN1),0)) "cule"
        				from 
          					${tableName} 
        				where 
          					MONTH_ID =#{oleDate}
       					and 
          					INDEX_LVL_1 in (${auditTypes})

							and ${where}


							and AREA_ID=#{orgId}

        				group by 
							INDEX_LVL_1 
					)
    			) t1 
    			join 
    				aud_audit_model t2
    			on 
    				t1."model" = t2.MODEL_ID
				where 
					t2.AUDIT_TYPE=#{auditType}
				and 
					t2.model_status='3'
    		) b 
    		on 
    			a. "model" = b."model" 
    		order by  
    			to_number("mom") desc,to_number("cule")desc 
    	)where rownum = 1

select 
	INDEX_LVL_1 "model",
	to_char(nvl(sum(COLUMN1),0)) "cule"
from 
	${tableName} 
where 
	MONTH_ID = #{date}
and 
	INDEX_LVL_1 = #{conType}
and 
	INDEX_LVL_1 in (${auditTypes})

	and ${where}


	and AREA_ID=#{orgId}

group by 
	INDEX_LVL_1

select MONTH_ID "date", to_char(nvl(sum(COLUMN1),0)) "cule" from ${tableName} where to_number(MONTH_ID)<= to_number(#{date}) and to_number(MONTH_ID)>= to_number(#{oleDate}) and INDEX_LVL_1 = #{conType} and INDEX_LVL_1 in (${auditTypes})
and ${where}
and AREA_ID=#{orgId}
group by MONTH_ID order by to_number(MONTH_ID)

	select 
		* 
	from(
		select 
			a."add",a."cule",a."name" ,to_char((a."cule"-b."cule")/b."cule") "mom"
		from(
			select 
				t1."add",t1."cule",t2.name "name" 
			from(
				select 
					* 
				from(
					select 
						AREA_ID "add",to_char(nvl(sum(COLUMN1),0)) "cule"
					from 
						${tableName} 
					where 
						MONTH_ID = #{date}
					and 
						INDEX_LVL_1 in (${auditTypes})

						and ${where}

					group by 
						AREA_ID 
				)
			) t1 
			join 
				sys_org t2
			on
				t1."add"=t2.ORG_ID
		)a left join(
			select 
				t1."add",t1."cule",t2.name "name" 
			from(
				select 
					* 
				from(
					select 
						AREA_ID "add",to_char(nvl(sum(COLUMN1),0)) "cule"
					from 
						${tableName} 
					where 
						MONTH_ID = #{oleDate}
					and 
						INDEX_LVL_1 in (${auditTypes})

						and ${where}

					group by 
						AREA_ID 
				)
			) t1 
			join 
				sys_org t2
			on 
				t1."add"=t2.ORG_ID
		)b 
		on 
			a."add"=b."add"
		order by 
			to_number("mom") desc,to_number("cule")desc
	)


        	where  "add"=#{orgId}


        	where rownum = 1

select AREA_ID "add", to_char(nvl(sum(COLUMN1),0)) "cule" from ${tableName} where MONTH_ID = #{date} and AREA_ID = #{add} and INDEX_LVL_1 in (${auditTypes})
and ${where}
group by AREA_ID

select MONTH_ID "date", to_char(nvl(sum(COLUMN1),0)) "cule" from ${tableName} where to_number(MONTH_ID)<= to_number(#{date}) and to_number(MONTH_ID)>= to_number(#{oleDate}) and AREA_ID = #{add} and INDEX_LVL_1 in (${auditTypes})
and ${where}
group by MONTH_ID order by to_number(MONTH_ID)

select t1."orgId",t2.name "name" from( select "orgId" from ( select a.DATA_AREA_ID "orgId",nvl(( b.numb/a.numb),0) "nu" from( select DATA_AREA_ID ,count(1) numb from WF_WORKORDER where AUDIT_CYCLE = #{date}
and DATA_AREA_ID=#{orgId}
group by DATA_AREA_ID ) a Left join( select DATA_AREA_ID ,count(1) numb from WF_WORKORDER where AUDIT_CYCLE = #{date} and status ='3'
and DATA_AREA_ID=#{orgId}
group by DATA_AREA_ID ) b on a.DATA_AREA_ID = b.DATA_AREA_ID order by to_number("nu") )
where "orgId"=#{orgId}
where rownum = 1
)t1 join sys_org t2 on t1."orgId" = t2.ORG_ID

select sum(COLUMN1) from ${tableName} where MONTH_ID= #{date,jdbcType=VARCHAR} and INDEX_LVL_1 in (${auditTypes})
and ${where}
group by MONTH_ID
